frontApp.controller("menuCategoryPostController",["$scope","$http","$window","CategoryPost",function(n,t,i,r){n.categories=[];n.posts=[];t.get("/API/CategoriesAPI/").success(function(t){var i=r.getallCategory(t);angular.forEach(i,function(t){t.idCategoryParent==1&&n.categories.push(t)})});t.get("/API/PostsAPI/").success(function(t){var i=t.length,r=0,u=Math.floor(Math.random()*i+1),f=Math.floor(Math.random()*i+1),e=Math.floor(Math.random()*i+1);angular.forEach(t,function(t,i){t.featured==1&&(i==u||i==f||i==e)&&r<4&&(r++,n.posts.push(t))})})}]);